import { useState,useEffect } from 'react'
import { Switch } from '@headlessui/react'
import axios from 'axios'

export default function ToggleButton(props) {
    console.log(props)
  const [enabled, setEnabled] = useState(false)
  useEffect(()=>{
    props.task.enabled=enabled
    axios.post(`${process.env.REACT_APP_API_ADDRESS}/task/status`,{
        "filter":{
            "name":props.task.task.name
        },
        "update": {"session_done" : 1}
    }).then((response)=>{
        console.log(response)
    }).catch(error=>{
        console.log(error)
    })

  },[enabled])
  return (
    <Switch
      checked={enabled}
      onChange={()=>setEnabled(!enabled)}
      className={`${
        enabled ? 'bg-green-600' : 'bg-gray-700'
      } relative inline-flex items-center h-5 rounded-full w-10`}
    >
      <span className="sr-only">Enable notifications</span>
      <span
        className={`${
          enabled ? 'translate-x-5' : 'translate-x-1'
        } inline-block w-3.5 h-3.5 transform bg-green-100 rounded-full ease-in-out duration-200`}
      />
    </Switch>
  )
}
